property.value:
WITH RECURSIVE links AS ( SELECT p.entity_id, p.entity_name, p.external_id, p.class_name, p.class_id, p.parent_id, p.field_name, p.field_type, p.field_value, p.data, p.external_class_name FROM (SELECT entity_id, external_id, entity_name, c.class_id, class_name, parent_id, data, key as field_name, regexp_replace(regexp_replace(value->>'type', '(:ID)|(Array\[)', ''), ']', '') as field_type, CASE WHEN value->>'value' LIKE '[%' THEN jsonb_array_elements_text((value->>'value')::jsonb) ELSE value->>'value' END AS field_value, c.external_class_name AS external_class_name FROM metadata_entity m LEFT JOIN metadata_entity_class c ON c.class_id = m.class_id, jsonb_each(data) WHERE m.entity_id IN (:IDS)) AS p
UNION
SELECT d.entity_id, d.entity_name, d.external_id, d.class_name, d.class_id, d.parent_id, d.field_name, d.field_type, d.field_value, d.data, d.external_class_name FROM (SELECT entity_id, external_id, entity_name, c.class_id, class_name, parent_id, data, key as field_name, regexp_replace(regexp_replace(value->>'type', '(:ID)|(Array\[)', ''), ']', '') as field_type, CASE WHEN value->>'value' LIKE '[%' THEN jsonb_array_elements_text((value->>'value')::jsonb) ELSE value->>'value' END AS field_value, c.external_class_name AS external_class_name FROM metadata_entity m LEFT JOIN metadata_entity_class c ON c.class_id = m.class_id, jsonb_each(data) WHERE m.parent_id = :PARENT_ID) AS d INNER JOIN links l ON l.parent_id = d.parent_id AND l.field_value = d.external_id AND l.field_type = d.class_name ) SELECT DISTINCT entity_id, entity_name, class_id, class_name, external_id, parent_id, data, external_class_name FROM links;
